import SwiftUI

struct DetailsView: View {
    
    let mushroom: Mushroom
    
    var body: some View {
        ZStack {
            Color.brown
                .ignoresSafeArea()
            VStack {
                Text(mushroom.name)
                    .font(.title)
                Text(mushroom.latinName)
                    .padding(.bottom)
                if let uiImage = try? mushroom.picture.getUiImage() {
                    Image(uiImage: uiImage )
                }
            }
        }
        
    }
}

#Preview {
    let mushroom = Mushroom(id: "2",
                            name: "Enoki",
                            latinName: "Flammulina filiformis",
                            picture: "data:image/jpeg;base64,")
    return DetailsView(mushroom: mushroom)
}
